import{_ as s,c as n,j as e,a,o}from"./chunks/framework.DPuwY6B9.js";const l="/assets/zawen.Cqjre7kQ.jpg",f=JSON.parse('{"title":"🍬 杂文","description":"","frontmatter":{"title":"🍬 杂文","outline":"deep","tags":"something"},"headers":[],"relativePath":"Notes/something/zawen.md","filePath":"Notes/something/zawen.md","lastUpdated":null}'),r={name:"Notes/something/zawen.md"};function i(p,t,c,d,m,u){return o(),n("div",null,t[0]||(t[0]=[e("p",null,[e("img",{src:l,alt:"加油"})],-1),e("blockquote",null,[e("p",null,[a("你要相信，每一场大雨，都会带来一次美丽的彩虹。"),e("br"),a("真正的英雄主义，那就是在认识生活的真相后依然热爱生活。")])],-1),e("p",null,"今天在抖音刷到离高考还剩50天，睡觉时竟突然多了很多关于之前的回忆，情绪涌上心头，打消了许多睡意，那就干脆写个小作文吧。恍惚间，感觉50天冲刺离自己很近，仿佛就在前几日，但又很远，远到已经过去了整整一年，365个日夜。茫然失措中已经快在大学摸爬滚打了一年了，对比上高三的自己，总觉得自己丧失了很多品质，喜欢那时为了梦想高校赌上一切的自己，那个从低谷一步一步克服畏惧挑战重拾信心的自己；喜欢那时早上从不赖床，靠着意志力充实每一天的自己；喜欢那时抱着不拼一次怎么知道自己的极限在哪里，最后释然参加高考的自己。记得60天（50还是60忘了）冲刺班会上，我被班长拉去发言给同学们打气，实在想不到怎么说的我，决定放一段前些天自己刚好刷到的一段张雪峰的考研励志演讲视频（高三一年自己确实改变了许多，晚自习回去不再是刷抖音而是看张雪峰的直播回放听他讲志愿填报，刷他各种考研演讲视频），那个视频很长，有17分钟，我通过了班主任的同意，那也是我第一次在班级里当着所有人的面袒露心声，那个一心向往高处走，不甘平庸的心。 回想自己已经度过的大学岁月，真的觉得自己过的很浑浑噩噩，迷茫和焦虑始终占据着主旋律，尤其是上学期，也许是不适应大学生活节奏，或许也是不习惯旁边的人好像都变得比自己优秀，比自己有天赋有基础，最主要的是，比自己知道应该干什么。好像自己也开始养成了各种逃避问题，做事犹豫不决的习惯，渐渐的，曾经的惰性好像也在暗处悄然生长。也许迷茫就是青春的主旋律吧，听了太多人的建议，反而觉得坚定自己的想法更重要，抓住当前时间去学习自己认为有用的，也许自己真的错了，但是我们都还年轻，不是吗，错了就错了吧，大不了重头来过，总比原地踏步好。真的很想再次体验那种让自己每天很充实的日子。最好的高中永远在大学的回忆里，但是别让最好的自己留在了高三那年里🤪",-1)]))}const g=s(r,[["render",i]]);export{f as __pageData,g as default};
